import { MatSort, Sort } from '@angular/material/sort';
import { MatTableDataSource } from '@angular/material/table';
import { AfterViewInit, Component, ViewChild, OnInit, ElementRef,OnDestroy } from '@angular/core';
import { MatPaginator } from '@angular/material/paginator';
import { UserDataInterface } from '../models/user.model';
import { Store, select } from '@ngrx/store';
import { selectAllUser, selectUserTotal, selectUserError, selectUserLoading } from '../state/user.selectors';
import { Observable, merge, Subject, Subscription } from 'rxjs';
import { tap, debounceTime, distinctUntilChanged } from 'rxjs/operators';
import { GlobalState } from '../state/global.state';
import { UserLoadAction } from '../state/user.actions';
import { UserParams } from '../models/user-params';
@Component({
  selector: 'app-user-table',
  templateUrl: './user-table.component.html',
  styleUrls: ['./user-table.component.css'],
})
export class UserTableComponent implements OnInit, OnDestroy, AfterViewInit {
  public displayedColumns: string[] = ['Id', 'FullName', 'Country', 'Role', 'Email'];
  public dataSource!: MatTableDataSource<UserDataInterface>;
  public userTotal: number;
  public noData: UserDataInterface[] = [<UserDataInterface>{}];
  public loading: boolean;
  public error$: Observable<boolean>;
  public defaultSort: Sort = { active: 'role', direction: 'asc' };
  public filterSubject = new Subject<string>();
  private subscription: Subscription = new Subscription();
  private filter: string = "";
  @ViewChild(MatPaginator) paginator: MatPaginator;
  @ViewChild(MatSort) sort: MatSort;

  constructor(public store: Store<GlobalState>) {
  }
  public ngOnInit(): void {
    this.store.pipe(select(selectAllUser)).subscribe(users => this.initializeData(users));
    this.store.pipe(select(selectUserTotal)).subscribe(total => this.userTotal = total);
    this.subscription.add(this.store.pipe(select(selectUserLoading)).subscribe(loading => {
      if (loading) {
        this.dataSource = new MatTableDataSource(this.noData);
      }
      this.loading = loading;
    }));
    this.error$ = this.store.pipe(select(selectUserError));
  }

  public ngAfterViewInit(): void {
    this.loadUsers();
    let filter$ = this.filterSubject.pipe(
      debounceTime(150),
      distinctUntilChanged(),
      tap((value: string) => {
        this.paginator.pageIndex = 0;
        this.filter = value;
      })
    );

    let sort$ = this.sort.sortChange.pipe(tap(() => this.paginator.pageIndex = 0));

    this.subscription.add(merge(filter$, sort$, this.paginator.page).pipe(
      tap(() => this.loadUsers())
    ).subscribe());
  }

  private loadUsers(): void {
    this.store.dispatch(new UserLoadAction(
      <UserParams>{
        filter: this.filter.toLocaleLowerCase(),
        pageIndex: this.paginator.pageIndex,
        pageSize: this.paginator.pageSize,
        sortDirection: this.sort.direction,
        sortField: this.sort.active
      }
    ));
  }

  private initializeData(users: UserDataInterface[]): void {
    this.dataSource = new MatTableDataSource(users.length ? users : this.noData);
  }

  public ngOnDestroy(): void {
    this.subscription.unsubscribe();
  }

  public retry(): void { 
    this.loadUsers();
  }

}
<div class="wrapper">
  <mat-card class=""
    ><h2 >User Management Pagination</h2>
    <div class="dc-h3 header">
      Users ({{userTotal}})
    </div>
    <mat-form-field appearance="standard">
      <mat-label>Filter</mat-label>
      <input
        matInput
        (keyup)="filterSubject.next($event.target.value)"
        placeholder="Type to search"
        #input
      />
    </mat-form-field>
    <div class="mat-elevation-z8">
      <table mat-table [dataSource]="dataSource" matSort  matSortDisableClear="true" >
        <!-- <ng-container *ngFor="let columnName of displayedColumns" [matColumnDef]="columnName">
          <th mat-header-cell *matHeaderCellDef>{{ columnName }}</th>
          <td mat-cell *matCellDef="let row">{{ row[columnName] }}</td>
        </ng-container> -->
        <ng-container matColumnDef="Id">
          <th mat-header-cell class="dc-label" *matHeaderCellDef mat-sort-header>Id</th>
          <td mat-cell class="dc-content" *matCellDef="let element">{{element.Id}} </td>
        </ng-container>

        <ng-container matColumnDef="Role">
          <th mat-header-cell class="dc-label" *matHeaderCellDef mat-sort-header>Role</th>
          <td mat-cell class="dc-content" *matCellDef="let user">{{user.Role}} </td>
        </ng-container>

        <ng-container matColumnDef="FullName">
          <th mat-header-cell class="dc-label" *matHeaderCellDef mat-sort-header>Full Name</th>
          <td mat-cell class="dc-content" *matCellDef="let element"> {{ element.FullName }} </td>
        </ng-container>

        <ng-container matColumnDef="lastName">
          <th mat-header-cell class="dc-label" *matHeaderCellDef mat-sort-header>Email</th>
          <td mat-cell class="dc-content" *matCellDef="let element">{{element.Email}} </td>
        </ng-container>

        <ng-container matColumnDef="amount">
            <th mat-header-cell class="dc-label" *matHeaderCellDef mat-sort-header>Country</th>
            <td mat-cell class="dc-content" *matCellDef="let element">{{element.Country}} </td>
          </ng-container>


          <ng-container matColumnDef="noData">
            <td mat-cell class="dc-label no-data-cell" *matCellDef="let element"
              [attr.colspan]="displayedColumns.length">
              <ng-container *ngIf="!(error$ | async) && !loading">
                <span class="dc-content">No Users</span>
              </ng-container>
              <ng-container *ngIf="(error$ | async) && !loading">
                <span class="dc-label">System Failure</span>
                <span class="dc-content">Error</span>
                <span class="retry" (click)="retry()">Retry</span>
              </ng-container>
              <ng-container *ngIf="loading">
                  <span class="dc-label">Retrieving data...</span>  
                </ng-container>
            </td>
          </ng-container>


        <tr mat-header-row *matHeaderRowDef="displayedColumns"></tr>
        <ng-container *ngIf="userTotal && !loading">
          <tr mat-row *matRowDef="let row; columns: displayedColumns;" class="user-row"></tr>
        </ng-container>
        <ng-container *ngIf="!userTotal || loading">
          <tr mat-row *matRowDef="let row; columns: ['noData'];"class="no-data-row" ></tr>
        </ng-container>
      </table>

      <mat-paginator
